import { Box, Typography, Card, Divider, Button } from "@mui/material";
import Chatbot from "../components/Chatbot";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import { useState } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import { PictureAsPdf as PdfIcon } from "@mui/icons-material";

function Report() {
  const [responses, setResponses] = useState([]);

  const handleChatSubmit = async (input) => {
    try {
      const response = await axios.post(
        "https://si8zczq3z3.execute-api.us-west-2.amazonaws.com/dummyReport",
        { query: input }
      );
      
      if (response.data?.highcharts_code) {
        const highchartsCode = response.data.highcharts_code.replace("const options = ", "").trim();
        let parsedOptions = null;
        try {
          parsedOptions = JSON.parse(highchartsCode);
        } catch (parseError) {
          console.error("Error parsing highcharts_code:", parseError, highchartsCode);
          return;
        }

        const newResponse = {
          chartOptions: parsedOptions,
          insights: response.data?.insights || "",
        };
        setResponses((prev) => [...prev, newResponse]);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handleDownloadReport = () => {
    const doc = new jsPDF();
    let yOffset = 10;
    responses.forEach((res, index) => {
      doc.text(`Report ${index + 1}`, 10, yOffset);
      yOffset += 10;
      doc.text(res.insights, 10, yOffset, { maxWidth: 180 });
      yOffset += 30;
    });
    doc.save("report.pdf");
  };

  return (
    <Box sx={{ height: "85vh", padding: 2, display: "flex", flexDirection: "column", justifyContent: "flex-end", overflowY: "auto" }}>
      {responses.map((res, index) => (
        <Card key={index} sx={{ p: 2, mb: 2 }}>
          <HighchartsReact highcharts={Highcharts} options={res.chartOptions} />
          <Divider sx={{ my: 2 }} />
          <Typography>{res.insights}</Typography>
        </Card>
      ))}
      <Box sx={{ p: 3 }}>
        <Typography variant="h5">Report</Typography>
        <Chatbot onChatSubmit={handleChatSubmit} />
        {responses.length > 0 && (
          <Button variant="contained" startIcon={<PdfIcon />} onClick={handleDownloadReport} sx={{ mt: 2 }}>
            Download Report
          </Button>
        )}
      </Box>
    </Box>
  );
}

export default Report;
