import React, { useState } from "react";
import { Box, TextField, Button, Modal, Typography, Paper } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";

const Chatbot = () => {
  const [message, setMessage] = useState("");
  const [open, setOpen] = useState(false);

  const handleSend = () => {
    if (message.trim()) {
      setOpen(true);
    }
  };

  return (
    <Box sx={{ position: "fixed", bottom: 20, right: 20, width: 300, p: 2 }}>
      {/* Chat Input Box */}
      <Paper sx={{ p: 2, display: "flex", alignItems: "center", gap: 1, borderRadius: 3 }}>
        <TextField
          fullWidth
          variant="outlined"
          size="small"
          placeholder="Ask me something..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <Button variant="contained" color="primary" onClick={handleSend}>
          <SendIcon />
        </Button>
      </Paper>

      {/* Modal Window */}
      <Modal open={open} onClose={() => setOpen(false)}>
        <Box
          sx={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: 400,
            bgcolor: "background.paper",
            p: 3,
            borderRadius: 2,
            boxShadow: 24,
          }}
        >
          <Typography variant="h6">Chat Response</Typography>
          <Typography sx={{ mt: 2 }}>{message}</Typography>
          <Button sx={{ mt: 2 }} variant="contained" onClick={() => setOpen(false)}>
            Close
          </Button>
        </Box>
      </Modal>
    </Box>
  );
};

export default Chatbot;
