import { Box, Typography, Card } from "@mui/material";
import Chatbot from "../components/Chatbot";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import { useState } from "react";
import axios from "axios";

function Report() {
  const [chartOptions, setChartOptions] = useState(null);
  const [insights, setInsights] = useState("");

  const handleChatSubmit = async (input) => {
    try {
      const response = await axios.post(
        "https://si8zczq3z3.execute-api.us-west-2.amazonaws.com/dummyReport",
        { query: input }
      );

    // const response = {
    //     data : {
    //         highcharts_code : {
    //             chart: {
    //                 type: 'bar'
    //             },
    //             title: {
    //                 text: 'Revenue vs PE Ratio Analysis'
    //             },
    //             xAxis: {
    //                 categories: ['Company 30', 'Company 29', 'Company 28', 'Company 27', 'Company 26', 'Company 25', 'Company 24', 'Company 22', 'Company 21', 'Company 20', 'Company 19', 'Company 18', 'Company 17', 'Company 16', 'Company 15', 'Company 14', 'Company 13', 'Company 12', 'Company 11', 'Company 10', 'Company 9', 'Company 8', 'Company 7', 'Company 6', 'Delta Tech', 'Beta Ltd', 'Gamma Inc', 'Epsilon Group', 'Alpha Corp'],
    //                 title: {
    //                     text: 'Companies'
    //                 }
    //             },
    //             yAxis: [{
    //                 title: {
    //                     text: 'Revenue'
    //                 }
    //             }, {
    //                 title: {
    //                     text: 'PE Ratio'
    //                 },
    //                 opposite: true
    //             }],
    //             tooltip: {
    //                 shared: true
    //             },
    //             plotOptions: {
    //                 bar: {
    //                     dataLabels: {
    //                         enabled: true
    //                     }
    //                 }
    //             },
    //             series: [{
    //                 name: 'Revenue',
    //                 type: 'column',
    //                 yAxis: 0,
    //                 data: [2000000, 1950000, 1900000, 1850000, 1800000, 1750000, 1700000, 1600000, 1550000, 1500000, 1450000, 1400000, 1350000, 1300000, 1250000, 1200000, 1150000, 1100000, 1050000, 1000000, 950000, 900000, 850000, 800000, 800000, 700000, 600000, 550000, 500000]
    //             }, {
    //                 name: 'PE Ratio',
    //                 type: 'line',
    //                 yAxis: 1,
    //                 data: [15, 14, 13, 12, 11, 10, 30, 30, 30, 30, 29, 28, 27, 26, 25, 24, 23, 22, 21, 20, 19, 18, 17, 16, 28, 22, 20, 18, 25]
    //             }]
    //         },
    //         insights : "Insights:\n1. Revenue ranges from $500,000 (Alpha Corp) to $2,000,000 (Company 30).\n2. P/E ratios vary between 10 (Company 25) and 30 (Companies 20-24).\n3. No clear direct correlation between revenue and P/E ratio.\n4. Companies with similar revenues can have significantly different P/E ratios (e.g., Delta Tech and Company 6 both have $800,000 revenue but P/E ratios of 28 and 16 respectively).\n5. Mid-revenue companies (around $1.5M-$1.7M) tend to"
    //     }
    // }
      
      if (response.data?.highcharts_code) {
        const highchartsCode = response.data.highcharts_code.replace("const options = ", "");
        console.log(highchartsCode, ' =========', typeof highchartsCode);
        
        const parsedOptions = JSON.parse(highchartsCode.replaceAll("\n", ""));
        // const parsedOptions = response.data.highcharts_code
        console.log(parsedOptions, '+++++++');
        
        setChartOptions(parsedOptions);
      }
      
      if (response.data?.insights) {
        setInsights(response.data.insights);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <Box sx={{ height: "85vh", padding: 2, display: "flex", flexDirection: "column", justifyContent: "flex-end" }}>
      {chartOptions && (
        <Card sx={{ p: 2, mb: 2 }}>
          <HighchartsReact highcharts={Highcharts} options={chartOptions} />
          {insights && <Typography sx={{ mt: 2 }}>{insights}</Typography>}
        </Card>
      )}
      <Box sx={{ p: 3 }}>
        <Typography variant="h5">Report</Typography>
        <Chatbot onChatSubmit={handleChatSubmit} />
      </Box>
    </Box>
  );
}

export default Report;
